def is_prime(n):
    if n < 2:
        return False
    for i in range(2, n):
        return n % i != 0
    return True

def find_duplicates(lst):
    seen = set()
    duplicates = []
    for item in lst:
        if item in seen:
            duplicates.append(item)
        seen.add(item)
    return duplicates
